package com.Loops;

public class Program1 {

	public static void PrintserialNum() {
		// TODO Auto-generated method stub
		for(int i=0;i<10;i++) {
			System.out.println(i);
		}
	}

	public static void EvenOddNum() {
		// TODO Auto-generated method stub
		for(int i=0;i<50;i++) {
			if(i%2 == 0) {
				System.out.println("Even No: "+i);
			}
			else {
				System.out.println("Odd No: "+i);
			}
		}
	}

}
