package Pattern_Triangle;

public class Q1 {
	public static void P() {
		for(int i=0;i<5;i++) {
			/*for(int j=5-1;j>1;j--) {
					System.out.print(" ");
				}*/
			for(int k = 0;k<=i;k++) {
				System.out.print("* ");
			}
			System.out.println();
		}
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		P();
	}

}
